import { useEffect, useState } from 'react';
import './App.css';
import Goal from './components/goal';
import MoreInfo from './components/moreinfo';

// importera all FNs info fran API 
// MAppa dem
// Med click man ska fa mer info som sub-mal (target)

function App() {


  const [goalsFromAPI, setGoalsFromAPI]= useState([]); 
  
  const [goalTitle,  setGoalTitle]= useState('');

  useEffect(()=>{
    async function fetchData(){
    const response= await fetch('https://unstats.un.org/SDGAPI/v1/sdg/Goal/List?includechildren=true');
    const data = await response.json(); 
    setGoalsFromAPI(data)
    console.log(data);
  }
  fetchData();
  }, []);
  
  
  function showGoal (show){
setGoalTitle(show)

  }
  function close(Close){
    setGoalTitle(Close)
  }
 


  return (
    <div className="App">
     <h1>FN:s Globala Target </h1>
    <MoreInfo data={goalTitle}/>
       <MoreInfo close= {close} data={goalTitle} /> 
    //  {goalsFromAPI.map(goals => <Goal titleobj={goals} key={goals.code } showGoal={showGoal}/>)}}

  </div>
  );
  }

export default App;
